+    const startMarker = performance.mark(`${metricName}:start`, {
+      startTime: event.timeStamp
+    });
+
+    const endMarker = performance.mark(`${metricName}:end`);
+
+    performance.measure(
+      benchmarkName,
+      `${metricName}:start`,
+      `${metricName}:end`
+    );
+  }
