  text-align: center;
  font-size: 1.2rem;
  color: #457942;
  margin-top: 5px;
}

/* CTA Button Section */
.cta-container {
  text-align: center;
  margin-top: 15px;
}

/* Call-to-Action Button with Shadow */
.cta-button {
  display: inline-block;
  padding: 12px 24px;
  background-color: #8B5E3B; /* Brown from Dawn */
  color: white;
  text-decoration: none;
  font-weight: bold;
  border-radius: 5px;
  margin-top: 10px;
  box-shadow: 2px 4px 8px rgba(0, 0, 0, 0.2); /* Subtle shadow */
  transition: all 0.3s ease-in-out;
}

/* Button Hover Effect */
.cta-button:hover {
  background-color: #457942;
  box-shadow: 3px 6px 12px rgba(0, 0, 0, 0.3); /* Stronger shadow on hover */
}
